import fetch, { RequestInit, Response } from 'node-fetch';

const OPTIONS: RequestInit = {
  headers: {
    Accept: 'text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,image/apng,*/*;q=0.8',
    'Accept-Encoding': 'gzip, deflate, br',
    'User-Agent':
      'Mozilla/5.0 (Linux; Android 8.0; Pixel 2 Build/OPD3.170816.012) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/75.0.3770.100 Mobile Safari/537.36',
  },
  compress: true,
  follow: 2,
  timeout: 20 * 1000,
};

/**
 * Retrieve via network the source of requested URLs.
 * @param urls
 */
export async function retrieve(urls: string[]): Promise<Response[]> {
  return await Promise.all(urls.map(url => fetch(url, OPTIONS)));
}
